import React, {Component} from 'react';
import Detail from "./Detail.js";

class Piece extends Component {

state = {
  showDetail: 0
};

getDetail = value => {
  this.setState({ showDetail: value });
};

hideDetail = value => {
  this.setState({ showDetail: 0 });
};

// Piece = (props) => {

render() {
  return (
          <div class="image-grid-block">
          <div className = "image-grid-image">
            <img src={props.portfolioItem.thumbnail.url} onClick={() => this.getDetail(props.portfolioPieces.id)}/>
            <Detail
              show={this.state.showDetail === props.portfolioPieces.id}
              onHide={() => this.hideDetail(props.portfolioPieces.id)}
            />
          </div>
            <div className = "image-grid-text">
              <p>{props.portfolioItem.title}</p>
              <p>{props.portfolioItem.caption}</p>
              </div>
            </div>
  );
}
}


export default Piece;
