import React, {Component } from "react";


// import "./Detail.css";

const Detail = (props) => {
    return (
      <div>
        {this.props.show && (
          <div className="detail-wrapper">
            <h1>{this.props.detailItem.title}</h1>
            <div>{this.props.detailItem.fullText}</div>
            <button onClick={this.props.onHide}>X</button>
          </div>
        )}
      </div>
    );
  }

export default Detail;
