import gql from "graphql-tag";
const PIECES_QUERY = gql`
    query {
        portfolioPieces {
            title
            imageAlt
            caption
            category
            thumbnail {
              id
              url
            }
            imagesMain {
              id
              url
            }
          }
        }
`;

export default PIECES_QUERY;
