import React, {Component} from 'react';
import Detail from "./Detail.js";

class Piece extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showDetail: 0
  }
}

getDetail = value => {
  this.setState({ showDetail: value });
};

hideDetail = value => {
  this.setState({ showDetail: 0 });
};



render() {

  const details = this.props.portfolioItem.imagesMain;

  return (

    <div class="image-grid-block">
      <div className = "image-grid-image">
        {details.map(detail => <Detail key = {detail.toString()} detailImg = {detail}  />)}
        <img src={this.props.portfolioItem.thumbnail.url} onClick={() => this.getDetail(this.props.portfolioItem.imagesMain.url.toString())}/>
        <Detail 
          show={this.state.showDetail === 1}
          onHide={() => this.hideDetail(1)}
          name={1}
          />
      </div> 
      <div className = "image-grid-text">
        <p>{this.props.portfolioItem.title}</p>
        <p>{this.props.portfolioItem.caption}</p>
      </div>
    </div>  
  )
}
}

export default Piece;
