import Detail from "./Detail.js";

class Piece extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showDetail: 0
  }
  }



getDetail = value => {
  this.setState({ showDetail: value });
};

hideDetail = value => {
  this.setState({ showDetail: 0 });
};

render(){
  return (
    // <div>  
      // Piece = (props) => (
          <div class="image-grid-block">
            <div className = "image-grid-image">
              <img src={props.portfolioItem.thumbnail.url} onClick={() => this.getDetail(props.portfolioItem.id)}/>
                <Detail
                  show={this.state.showDetail === props.portfolioItem.id}
                  onHide={() => this.hideDetail(props.portfolioItem.id)}
                />
            </div>
            <div className = "image-grid-text">
              <p>{props.portfolioItem.title}</p>
              <p>{props.portfolioItem.caption}</p>
            </div>
          </div>  
      )

    // </div>
  // );
}
}

export default Piece;
