import React, {Component} from "react";
import { Query } from 'react-apollo';
import PIECES_QUERY from './all-pieces/index';


// import "./Detail.css";

class Detail extends Component {
  constructor(props) {
    super(props)
    this.state = { counter: 0 };
  }

  render() {
    console.log(this.props.show);
    return (
      <Query query={PIECES_QUERY}>
       {({ loading, error, data }) => {

          if (loading) return <div>Fetching pieces.....</div>
          if (error)   return <div>Error fetching pieces</div>

          const detailImgs = data.portfolioPieces;


          return (
      <React.Fragment>
        {this.props.show && (
          <div className="detail-wrapper">

            {detailImgs.map(detailImg => <DetailImg key = {detailImg.toString()} detailURL={detailImg} />)}
            <h1>{this.props.detailImg.title}</h1>
            <div>{this.props.detailImg.fullText}</div>
            <button onClick={this.props.onHide}>X</button>
          </div>
        )}
      </React.Fragment>
    );
  }
}
</Query>
);
}
}

export default Detail;
