import React, {Component} from 'react';
import Detail from "./Detail.js";

class Piece extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showDetail: 0
  }
}

getDetail = value => {
  this.setState({ showDetail: value });
};

hideDetail = value => {
  this.setState({ showDetail: 0 });
};

render() {
  return (
    <div class="image-grid-block">
      <div className = "image-grid-image">
        {this.props.portfolioItem.imagesMain.map(detail => <Detail key = {detail.toString()} detailImg = {detail}  />)}
        <img src={this.props.portfolioItem.thumbnail.url} onClick={() => this.getDetail(2)}/>
        <Detail 
          show={this.state.showDetail === 3}
          onHide={() => this.hideModal(4)}
          name={5}
          />
      </div> 
      <div className = "image-grid-text">
        <p>{this.props.portfolioItem.title}</p>
        <p>{this.props.portfolioItem.caption}</p>
      </div>
    </div>  
  )
}
}

export default Piece;
